import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';

const Checkout = () => {
  const { cartItems, clearCart } = useContext(CartContext);
  const navigate = useNavigate(); // Usar para redirigir después del pago

  // Función para manejar el pago (simulado)
  const handleCheckout = () => {
    alert('Pago realizado con éxito. Gracias por su compra.');
    clearCart(); // Vaciar el carrito después del pago
    navigate('/'); // Redirigir a la página de inicio
  };

  // Calcular el total
  const getTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
  };

  return (
    <div>
      <h2>Resumen de Compra</h2>
      {cartItems.length > 0 ? (
        <div>
          <ul>
            {cartItems.map((item) => (
              <li key={item._id}>
                <h4>{item.name}</h4>
                <p>Precio: ${item.price}</p>
                <p>Cantidad: {item.quantity}</p>
              </li>
            ))}
          </ul>
          <h3>Total: ${getTotal().toFixed(2)}</h3> {/* Mostrar el total */}
          <button onClick={handleCheckout}>Realizar Pago</button> {/* Botón de pago */}
        </div>
      ) : (
        <p>No hay productos en el carrito.</p>
      )}
    </div>
  );
};

export default Checkout;
