import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Card, CardContent, Typography, Button, Grid } from '@mui/material';
import { CartContext } from '../context/CartContext';

const ProductDetail = ({ products }) => {
  const { id } = useParams();
  const product = products.find((p) => p._id === id);
  const { addToCart } = useContext(CartContext); // Obtener la función addToCart desde el contexto

  if (!product) {
    return <Typography variant="h5">Producto no encontrado</Typography>;
  }

  return (
    <Container maxWidth="sm" style={{ marginTop: '20px' }}>
      <Card>
        <CardContent>
          <Typography variant="h4" gutterBottom>
            {product.name}
          </Typography>
          <Typography variant="body1" color="textSecondary" paragraph>
            {product.description}
          </Typography>
          <Typography variant="h6" color="textSecondary">
            Precio: ${product.price}
          </Typography>
          <img src={product.image} alt={product.name} width="100%" style={{ maxHeight: '300px', objectFit: 'cover' }} />
          <Grid container spacing={2} style={{ marginTop: '20px' }}>
            <Grid item>
              <Button variant="contained" color="primary" onClick={() => addToCart(product)}>
                Agregar al Carrito
              </Button>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Container>
  );
};

export default ProductDetail;
