import React from 'react';
import { Card, CardContent, CardActions, Typography, Button, Grid, Container } from '@mui/material';
import api from '../api'; // Importa el archivo api para realizar las peticiones al backend

const ProductList = ({ products, onDelete, onEdit }) => {
  const handleDelete = async (id) => {
    try {
      await api.delete(`/products/${id}`);
      alert('Producto eliminado con éxito');
      onDelete();
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
    }
  };

  return (
    <Container maxWidth="md" style={{ marginTop: '20px' }}>
      <Typography variant="h5" gutterBottom>
        Lista de Productos
      </Typography>
      <Grid container spacing={3}>
        {products.length > 0 ? (
          products.map((product) => (
            <Grid item xs={12} sm={6} md={4} key={product._id}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{product.name}</Typography>
                  <Typography variant="body2">{product.description}</Typography>
                  <Typography variant="h6" color="text.secondary">
                    Precio: ${product.price}
                  </Typography>
                  <img src={product.image} alt={product.name} width="100%" style={{ maxHeight: '200px', objectFit: 'cover' }} />
                </CardContent>
                <CardActions>
                  <Button size="small" color="primary" onClick={() => onEdit(product)}>
                    Editar
                  </Button>
                  <Button size="small" color="secondary" onClick={() => handleDelete(product._id)}>
                    Eliminar
                  </Button>
                  <Button size="small" color="default">
                    Ver Detalles
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))
        ) : (
          <Typography variant="body1">No hay productos disponibles.</Typography>
        )}
      </Grid>
    </Container>
  );
};

export default ProductList;
