import React, { useState, useEffect } from 'react';
import CreateProduct from '../components/CreateProduct';
import ProductList from '../components/ProductList';
import api from '../api';
import UpdateProduct from '../components/UpdateProduct'; // Importa UpdateProduct

const Home = () => {
  const [products, setProducts] = useState([]);

  const fetchProducts = async () => {
    try {
      const response = await api.get('/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener los productos:', error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Welcome to your Store</h1>
      <CreateProduct onProductCreated={fetchProducts} /> {/* Pasar función */}
      <ProductList products={products} onDelete={fetchProducts} /> {/* Pasar función de actualización */}
    </div>
  );
};

export default Home;
