import React, { useState } from 'react';
import api from '../api';

const ProductList = ({ products, onDelete, onEdit }) => {
  const [searchTerm, setSearchTerm] = useState(''); // Estado para el término de búsqueda

  // Función para manejar el cambio en el input de búsqueda
  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  // Filtrar los productos según el término de búsqueda
  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDelete = async (id) => {
    try {
      await api.delete(`/products/${id}`);
      alert('Producto eliminado con éxito');
      onDelete(); // Llamar a la función para actualizar la lista
    } catch (error) {
      console.error('Error al eliminar el producto:', error);
    }
  };

  return (
    <div>
      <h2>Lista de Productos</h2>

      {/* Campo de búsqueda */}
      <input
        type="text"
        placeholder="Buscar productos..."
        value={searchTerm} // Valor del input de búsqueda
        onChange={handleSearch} // Llamar a la función handleSearch cuando el valor cambie
        style={{ marginBottom: '20px', padding: '8px', width: '200px' }} // Estilos opcionales
      />

      {filteredProducts.length > 0 ? (
        <ul>
          {filteredProducts.map((product) => (
            <li key={product._id}>
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              <p>Precio: ${product.price}</p>
              <img src={product.image} alt={product.name} width="100" />
              <button onClick={() => handleDelete(product._id)}>Eliminar</button> {/* Botón de eliminar */}
              <button onClick={() => onEdit(product)}>Editar</button> {/* Botón de editar */}
            </li>
          ))}
        </ul>
      ) : (
        <p>No hay productos disponibles.</p>
      )}
    </div>
  );
};

export default ProductList;
