import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './pages/Home';
import ProductDetail from './pages/ProductDetail';
import Cart from './components/Cart'; // Importar el componente de carrito
import { CartProvider } from './context/CartContext'; // Importar el CartProvider

function App() {
  return (
    <CartProvider> {/* Envolver la aplicaci√≥n en CartProvider */}
      <Router>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/product/:id" element={<ProductDetail />} />
          <Route path="/cart" element={<Cart />} /> {/* Ruta para el carrito */}
        </Routes>
      </Router>
    </CartProvider>
  );
}

export default App;
