import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Cambiamos la importación a BrowserRouter
import Home from './pages/Home';
import ProductDetail from './pages/ProductDetail';
import Cart from './pages/Cart';
import Checkout from './pages/Checkout'; // Asegúrate de importar Checkout correctamente
import { CartProvider } from './context/CartContext';
import Navbar from './components/Navbar';

function App() {
  return (
    <CartProvider>
      <Router> {/* Cambiar Router aquí */}
        <Navbar /> {/* Incluir el Navbar */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/product/:id" element={<ProductDetail />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/checkout" element={<Checkout />} /> {/* Definir la ruta para Checkout */}
        </Routes>
      </Router>
    </CartProvider>
  );
}

export default App;
