import React, { useState } from 'react';
import api from '../api';

const CreateProduct = ({ onProductCreated }) => { // Asegúrate de recibir la prop onProductCreated

  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Crear un nuevo producto
      const newProduct = { name, description, price, image };
      await api.post('/products', newProduct);
      alert('Producto creado con éxito');
      // Limpiar los campos después de crear el producto
      setName('');
      setDescription('');
      setPrice('');
      setImage('');

// Llamar a la función onProductCreated para actualizar la lista de productos en el componente padre
if (onProductCreated) {
  onProductCreated(); // Asegurarse de que la función sea llamada después de crear el producto

}
    } catch (error) {
      console.error('Error al crear el producto:', error);
    }
  };

  return (
    <div>
      <h2>Create New Product</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div>
          <label>Description:</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div>
          <label>Price:</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <div>
          <label>Image:</label>
          <input
            type="text"
            value={image}
            onChange={(e) => setImage(e.target.value)}
          />
        </div>
        <button type="submit">Create Product</button>
      </form>
    </div>
  );
};

export default CreateProduct;
